import { DECREASE, INCREASE } from './..'

const countReducer = ( state, action) => {
    switch(action.type){
        case DECREASE:
            return {...state, count: state.count - 1}
        case INCREASE:
            return {...state, count: state.count + 1}
        default:
            return state
    }
}
