import { DECREASE, INCREASE, CLEAR_CART } from '../action'

const reducer = ( state, action) => {
    switch(action.type){
        case DECREASE:
            return {...state, count: state.count - 1}
        case INCREASE:
            return {...state, count: state.count + 1}
        case CLEAR_CART:
            return {...state, amount: state}
        default:
            return state
    }
}

export default reducer
