import { DECREASE, INCREASE, CLEAR_CART, REMOVE } from '../action'

const reducer = ( state, action) => {
    switch(action.type){
        case DECREASE:
            return {...state, count: state.count - 1}
        case INCREASE:
            return {...state, count: state.count + 1}
        case CLEAR_CART:
            return {...state, cart: []}
        case REMOVE:
            console.log(action.id)
            break
        default:
            return state
    }
}

export default reducer
